$().ready( function() {
    $("#account_login").click(function () {
        $(this).addClass("login_title_current").siblings(".login_title").removeClass("login_title_current");
        $(".account_login").show().siblings(".login_content").hide();
    });
    $("#wechat_login").click(function () {
        $(this).addClass("login_title_current").siblings(".login_title").removeClass("login_title_current");
        $(".wechat_login").show().siblings(".login_content").hide();
    });
	var $loginForm = $("#loginForm"), $submit = $loginForm.find(":submit"), $flashMessage = $("#flashMessage"), $remember = $("#remember");
	var $username = $("#username"), $password = $("#password"), $captcha = $("#captcha");
    var $corp = "false";
	$submit.prop("disabled", false);
    $(":radio").change(function(){
        $corp = $("input:checked").val();
    });
	$loginForm.validate({
		errorClass: "errorMessage",
		rules: {
			username: {
				required: true,
				pattern: /^[0-9a-z_A-Z\u4e00-\u9fa5]+$/,
				minlength: 2
			},
			password: {
				required: true,
				minlength: 4
			}
			,
			captcha: {
					required: true
			}
		},
		messages: {
			username: {
				required: "请输入用户名",
				pattern: "用户名只允许包含中文、英文、数字、下划线",
				minlength: "用户名最小长度为2"
			},
			password: {
				required: "请输入密码",
				minlength: "密码最小长度为4"
			}
			,
			captcha: {
				required: "请输入验证码"
			}
		},
		errorPlacement: function(error, element) {
			element.closest("li").find(".errorMessage").text(error.text());
			element.closest("li").find(".errorMessage").addClass("icon");
		},
		unhighlight: function(element) {
			$(element).closest("li").find(".errorMessage").text("");
			$(element).closest("li").find(".errorMessage").removeClass("icon");
		},
		submitHandler: function(form) {
			$submit.prop("disabled", true);
		
			var rsaKey = new RSAKey();
			rsaKey.setPublic(b64tohex(modules), b64tohex(exponent));
			
			$.ajax({
				url: ctx + "/admin/login",
				data: {
					username: $username.val(),
                    corp:$corp,
					password: hex2b64(rsaKey.encrypt($password.val()))
					,
					captcha: $captcha.val()
				},
				type: "post",
				dataType: "json",
				cache: false,
				beforeSend: function(request, settings) {
					request.setRequestHeader("token", $.cookie("token"));
					$submit.val(" 登 录 中 ... ");
				},
				success: function (result) {
					if ($remember.prop("checked")) {
						$.cookie("rememberAdmin", $username.val(), {expires: 7 * 24 * 60 * 60});
					} else {
						$.removeCookie("memberUsername");
					}
					if (result.status == "success") {
						var top = getTopWinow();
						top.location.href=result.data.successUrl;
					} else {
						$flashMessage.text(result.message).addClass("icon");
						modules = result.data.modules, exponent = result.data.exponent;
						$captcha.val("");
						$captchaImage.click();
						$submit.val(" 登 录 ");
						$submit.prop("disabled", false);
					}
				},
				error: function() {
					$flashMessage.text("登录失败，请刷新页面重试");
					$submit.val(" 登 录 失 败 ");
				}
			});
		}
	});

	var remember = $.cookie("rememberAdmin");
	if(remember != null) {
		$remember.prop("checked", true);
		$username.val(remember);
		$password.focus();
	} else {
		$remember.prop("checked", false);
		$username.focus();
	}
		
	var $captchaImage = $("#captchaImage");
	$captchaImage.click(function() {
		$captchaImage.prop("src", ctx + "/admin/captcha?type=ADMIN_LOGIN&timestamp=" + (new Date()).valueOf());
	});

});

function getTopWinow(){
	var p = window;
	while(p != p.parent){
		p = p.parent;
	}
	return p;
}